#!/usr/bin/env python3# 通过返回页面过滤翻译结果from pyquery import PyQuery as pqurl = 'https://fanyi.baidu.com/#auto/zh/%s' % 'love'headers = {    'Host': 'fanyi.baidu.com',    'Referer': 'https://fanyi.baidu.com/',    'User-Agent': 'Mozilla/5.0 (X11; Linux x86_64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/69.0.3497.92 Safari/537.36'}req = pq(url, headers=headers, timeout=10, encoding='utf-8')print(req.html())